    void shouldReturnCashChageOptions(final Integer troco, final String expected) {
        final var result = service.getCashExchangeOptions(troco);

        if (troco <= 10000) {
            final var textResult = formata(result.stream().findFirst().get());
            assertThat(textResult)
                .isEqualTo(expected);
        } else {
            assertThat(result)
                .isNotNull();
        }
    }

    private String formata(final Mix mix){
        StringBuilder result = new StringBuilder();
        mix.getChange().forEach(change -> {
            result.append((String.valueOf(change.getCoin())+", ").repeat(change.getQty()));
        });
        result.deleteCharAt(result.length()-2);
        return result.toString();
    }

    @ParameterizedTest
    @ArraySources(
        arrays = {
            @ArraySource(array = { 8, 5, 3, 2, 1 }),
            @ArraySource(array = { 8, 5, 3, 1 }),
            @ArraySource(array = { 8, 5, 2, 1 }),
            @ArraySource(array = { 8, 3, 2, 1 }),
            @ArraySource(array = { 5, 3, 2, 1 }),
            @ArraySource(array = { 8, 5, 1 }),
            @ArraySource(array = { 8, 3, 1 }),
            @ArraySource(array = { 8, 2, 1 })
        }
    )
    void test(final int[] coins) {
        final var result = service.getCashExchange(coins, 10);
        assertThat(result)
            .isNotNull();
    }

    @ParameterizedTest
    @MethodSource("severalInts")
    void testSeveralInts(Integer input) {
        final var result = service.getCashExchangeOptions(input);

        assertNotNull(result);
        assertFalse(result.isEmpty());
        assertEquals(input >= 3 ? 3: input, result.size());
    }

    private static IntStream severalInts() {
        return IntStream.concat(
            IntStream.range(1, 30000),
            IntStream.concat(
                IntStream.generate(() -> { return (int)(Math.random() * Integer.MAX_VALUE); }).limit(30000),
                (IntStream.rangeClosed(Integer.MAX_VALUE - 30000, Integer.MAX_VALUE))));
    }
